    function drawPixel(coord){
        triggerMouseEvent(c, "mousedown", coord[0]*scale, coord[1]*scale);
        triggerMouseEvent(c, "mouseup", coord[0]*scale, coord[1]*scale);
    }

    function triggerMouseEvent (node, eventType, x, y) {
        var clickEvent = document.createEvent ('MouseEvents');
        clickEvent.offsetX = 0;
        clickEvent.offsetY = 0;
        // clickEvent.initMouseEvent (eventType, true, true)
        // console.log("clickEvent",clickEvent);
        clickEvent.initMouseEvent (eventType, true, true, window, 0, 
            c.x + x, c.y + y, bounds.left + x, bounds.top + y, 
            false, false, false, false,  
            0, null);
        node.dispatchEvent (clickEvent);
    }

